class CashPeer extends BaseCashPeer 
{
  /**
   * Retrieve for select
   * 
   * @param string $q
   * @param int $limit
   * @return array object(Account) 
   */
  static public function retrieveForSelect($q, $limit, Agency $agency = null)
  {
    $criteria = new Criteria();
    
    if($agency != null){
      $criteria->add(self::AGENCY_ID, $agency->getId());
    }
    
    $criteria->add(self::NAME, '%'.$q.'%', Criteria::LIKE);
    $criteria->addAscendingOrderByColumn(self::NAME);
    $criteria->setLimit($limit);
 
    $cashs = array();
    foreach (self::doSelect($criteria) as $cash)
    {
      $cashs[$cash->getId()] = (string) $cash;
    }
 
    return $cashs;
  }
} // ConnectionPeer
