	/**
	 * Performs the work of inserting or updating the row in the database.
	 *
	 * If the object is new, it inserts it; otherwise an update is performed.
	 * All related objects are also updated in this method.
	 *
	 * @param      PropelPDO $con
	 * @return     int The number of rows affected by this insert/update and any referring fk objects' save() operations.
	 * @throws     PropelException
	 * @see        save()
	 */
	protected function doSave(PropelPDO $con)
	{
		$affectedRows = 0; // initialize var to track total num of affected rows
		if (!$this->alreadyInSave) {
			$this->alreadyInSave = true;

			// We call the save method on the following object(s) if they
			// were passed to this object by their coresponding set
			// method.  This object relates to these object(s) by a
			// foreign key reference.

			if ($this->aCash !== null) {
				if ($this->aCash->isModified() || $this->aCash->isNew()) {
					$affectedRows += $this->aCash->save($con);
				}
				$this->setCash($this->aCash);
			}

			if ($this->aUser !== null) {
				if ($this->aUser->isModified() || $this->aUser->isNew()) {
					$affectedRows += $this->aUser->save($con);
				}
				$this->setUser($this->aUser);
			}

			if ($this->aTransactionType !== null) {
				if ($this->aTransactionType->isModified() || $this->aTransactionType->isNew()) {
					$affectedRows += $this->aTransactionType->save($con);
				}
				$this->setTransactionType($this->aTransactionType);
			}

			if ($this->aCredit !== null) {
				if ($this->aCredit->isModified() || $this->aCredit->isNew()) {
					$affectedRows += $this->aCredit->save($con);
				}
				$this->setCredit($this->aCredit);
			}

			if ($this->aAccount !== null) {
				if ($this->aAccount->isModified() || $this->aAccount->isNew()) {
					$affectedRows += $this->aAccount->save($con);
				}
				$this->setAccount($this->aAccount);
			}

			if ($this->aBankbook !== null) {
				if ($this->aBankbook->isModified() || $this->aBankbook->isNew()) {
					$affectedRows += $this->aBankbook->save($con);
				}
				$this->setBankbook($this->aBankbook);
			}

			if ($this->aInvestment !== null) {
				if ($this->aInvestment->isModified() || $this->aInvestment->isNew()) {
					$affectedRows += $this->aInvestment->save($con);
				}
				$this->setInvestment($this->aInvestment);
			}

			if ($this->isNew() ) {
				$this->modifiedColumns[] = TransactionPeer::ID;
			}

			// If this object has been modified, then save it to the database.
			if ($this->isModified()) {
				if ($this->isNew()) {
					$pk = TransactionPeer::doInsert($this, $con);
					$affectedRows += 1; // we are assuming that there is only 1 row per doInsert() which
										 // should always be true here (even though technically
										 // BasePeer::doInsert() can insert multiple rows).

					$this->setId($pk);  //[IMV] update autoincrement primary key

					$this->setNew(false);
				} else {
					$affectedRows += TransactionPeer::doUpdate($this, $con);
				}

				$this->resetModified(); // [HL] After being saved an object is no longer 'modified'
			}

			if ($this->collPayments !== null) {
				foreach ($this->collPayments as $referrerFK) {
					if (!$referrerFK->isDeleted()) {
						$affectedRows += $referrerFK->save($con);
					}
				}
			}

			$this->alreadyInSave = false;

		}
		return $affectedRows;
	} // doSave()

