		if ($this->isColumnModified(TransactionPeer::TRANSACTION_TYPE_ID)) $criteria->add(TransactionPeer::TRANSACTION_TYPE_ID, $this->transaction_type_id);
		if ($this->isColumnModified(TransactionPeer::AMOUNT)) $criteria->add(TransactionPeer::AMOUNT, $this->amount);
		if ($this->isColumnModified(TransactionPeer::OBSERVATION)) $criteria->add(TransactionPeer::OBSERVATION, $this->observation);
		if ($this->isColumnModified(TransactionPeer::CREATED_AT)) $criteria->add(TransactionPeer::CREATED_AT, $this->created_at);
		if ($this->isColumnModified(TransactionPeer::CREDIT_ID)) $criteria->add(TransactionPeer::CREDIT_ID, $this->credit_id);
		if ($this->isColumnModified(TransactionPeer::ACCOUNT_ID)) $criteria->add(TransactionPeer::ACCOUNT_ID, $this->account_id);
		if ($this->isColumnModified(TransactionPeer::BANKBOOK_ID)) $criteria->add(TransactionPeer::BANKBOOK_ID, $this->bankbook_id);
		if ($this->isColumnModified(TransactionPeer::ACCOUNT_BALANCE)) $criteria->add(TransactionPeer::ACCOUNT_BALANCE, $this->account_balance);
