				// Add objects for joined Investment rows

				$key7 = InvestmentPeer::getPrimaryKeyHashFromRow($row, $startcol7);
				if ($key7 !== null) {
					$obj7 = InvestmentPeer::getInstanceFromPool($key7);
					if (!$obj7) {
	
						$cls = InvestmentPeer::getOMClass(false);

					$obj7 = new $cls();
					$obj7->hydrate($row, $startcol7);
					InvestmentPeer::addInstanceToPool($obj7, $key7);
				} // if $obj7 already loaded

				// Add the $obj1 (Transaction) to the collection in $obj7 (Investment)
				$obj7->addTransaction($obj1);

			} // if joined row is not null

			$results[] = $obj1;
		}
		$stmt->closeCursor();
		return $results;
	}


	/**
	 * Selects a collection of Transaction objects pre-filled with all related objects except Investment.
	 *
	 * @param      Criteria  $criteria
	 * @param      PropelPDO $con
	 * @param      String    $join_behavior the type of joins to use, defaults to Criteria::LEFT_JOIN
	 * @return     array Array of Transaction objects.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doSelectJoinAllExceptInvestment(Criteria $criteria, $con = null, $join_behavior = Criteria::LEFT_JOIN)
	{
		$criteria = clone $criteria;

		// Set the correct dbName if it has not been overridden
		// $criteria->getDbName() will return the same object if not set to another value
		// so == check is okay and faster
		if ($criteria->getDbName() == Propel::getDefaultDB()) {
			$criteria->setDbName(self::DATABASE_NAME);
		}

		TransactionPeer::addSelectColumns($criteria);
		$startcol2 = (TransactionPeer::NUM_COLUMNS - TransactionPeer::NUM_LAZY_LOAD_COLUMNS);

		CashPeer::addSelectColumns($criteria);
		$startcol3 = $startcol2 + (CashPeer::NUM_COLUMNS - CashPeer::NUM_LAZY_LOAD_COLUMNS);

		sfGuardUserPeer::addSelectColumns($criteria);
		$startcol4 = $startcol3 + (sfGuardUserPeer::NUM_COLUMNS - sfGuardUserPeer::NUM_LAZY_LOAD_COLUMNS);

		TransactionTypePeer::addSelectColumns($criteria);
		$startcol5 = $startcol4 + (TransactionTypePeer::NUM_COLUMNS - TransactionTypePeer::NUM_LAZY_LOAD_COLUMNS);

		CreditPeer::addSelectColumns($criteria);
		$startcol6 = $startcol5 + (CreditPeer::NUM_COLUMNS - CreditPeer::NUM_LAZY_LOAD_COLUMNS);

		AccountPeer::addSelectColumns($criteria);
		$startcol7 = $startcol6 + (AccountPeer::NUM_COLUMNS - AccountPeer::NUM_LAZY_LOAD_COLUMNS);

		BankbookPeer::addSelectColumns($criteria);
		$startcol8 = $startcol7 + (BankbookPeer::NUM_COLUMNS - BankbookPeer::NUM_LAZY_LOAD_COLUMNS);

		$criteria->addJoin(TransactionPeer::CASH_ID, CashPeer::ID, $join_behavior);

		$criteria->addJoin(TransactionPeer::USER_ID, sfGuardUserPeer::ID, $join_behavior);

		$criteria->addJoin(TransactionPeer::TRANSACTION_TYPE_ID, TransactionTypePeer::ID, $join_behavior);

		$criteria->addJoin(TransactionPeer::CREDIT_ID, CreditPeer::ID, $join_behavior);

		$criteria->addJoin(TransactionPeer::ACCOUNT_ID, AccountPeer::ID, $join_behavior);

		$criteria->addJoin(TransactionPeer::BANKBOOK_ID, BankbookPeer::ID, $join_behavior);

		// symfony_behaviors behavior
		foreach (sfMixer::getCallables(self::getMixerPreSelectHook(__FUNCTION__)) as $sf_hook)
		{
		  call_user_func($sf_hook, 'BaseTransactionPeer', $criteria, $con);
		}


		$stmt = BasePeer::doSelect($criteria, $con);
		$results = array();

		while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
			$key1 = TransactionPeer::getPrimaryKeyHashFromRow($row, 0);
			if (null !== ($obj1 = TransactionPeer::getInstanceFromPool($key1))) {
				// We no longer rehydrate the object, since this can cause data loss.
				// See http://propel.phpdb.org/trac/ticket/509
				// $obj1->hydrate($row, 0, true); // rehydrate
			} else {
				$cls = TransactionPeer::getOMClass(false);

				$obj1 = new $cls();
				$obj1->hydrate($row);
				TransactionPeer::addInstanceToPool($obj1, $key1);
			} // if obj1 already loaded

				// Add objects for joined Cash rows

				$key2 = CashPeer::getPrimaryKeyHashFromRow($row, $startcol2);
				if ($key2 !== null) {
					$obj2 = CashPeer::getInstanceFromPool($key2);
					if (!$obj2) {
	
						$cls = CashPeer::getOMClass(false);

					$obj2 = new $cls();
					$obj2->hydrate($row, $startcol2);
					CashPeer::addInstanceToPool($obj2, $key2);
				} // if $obj2 already loaded

				// Add the $obj1 (Transaction) to the collection in $obj2 (Cash)
				$obj2->addTransaction($obj1);

			} // if joined row is not null

				// Add objects for joined sfGuardUser rows

				$key3 = sfGuardUserPeer::getPrimaryKeyHashFromRow($row, $startcol3);
				if ($key3 !== null) {
					$obj3 = sfGuardUserPeer::getInstanceFromPool($key3);
					if (!$obj3) {
	
						$cls = sfGuardUserPeer::getOMClass(false);

					$obj3 = new $cls();
					$obj3->hydrate($row, $startcol3);
					sfGuardUserPeer::addInstanceToPool($obj3, $key3);
				} // if $obj3 already loaded

				// Add the $obj1 (Transaction) to the collection in $obj3 (sfGuardUser)
				$obj3->addTransaction($obj1);

			} // if joined row is not null

				// Add objects for joined TransactionType rows

				$key4 = TransactionTypePeer::getPrimaryKeyHashFromRow($row, $startcol4);
				if ($key4 !== null) {
					$obj4 = TransactionTypePeer::getInstanceFromPool($key4);
					if (!$obj4) {
	
						$cls = TransactionTypePeer::getOMClass(false);

					$obj4 = new $cls();
					$obj4->hydrate($row, $startcol4);
					TransactionTypePeer::addInstanceToPool($obj4, $key4);
				} // if $obj4 already loaded

				// Add the $obj1 (Transaction) to the collection in $obj4 (TransactionType)
				$obj4->addTransaction($obj1);

			} // if joined row is not null

				// Add objects for joined Credit rows

				$key5 = CreditPeer::getPrimaryKeyHashFromRow($row, $startcol5);
				if ($key5 !== null) {
					$obj5 = CreditPeer::getInstanceFromPool($key5);
					if (!$obj5) {
	
						$cls = CreditPeer::getOMClass(false);

					$obj5 = new $cls();
					$obj5->hydrate($row, $startcol5);
					CreditPeer::addInstanceToPool($obj5, $key5);
				} // if $obj5 already loaded

				// Add the $obj1 (Transaction) to the collection in $obj5 (Credit)
				$obj5->addTransaction($obj1);

			} // if joined row is not null

				// Add objects for joined Account rows

				$key6 = AccountPeer::getPrimaryKeyHashFromRow($row, $startcol6);
				if ($key6 !== null) {
					$obj6 = AccountPeer::getInstanceFromPool($key6);
					if (!$obj6) {
	
						$cls = AccountPeer::getOMClass(false);

					$obj6 = new $cls();
					$obj6->hydrate($row, $startcol6);
					AccountPeer::addInstanceToPool($obj6, $key6);
				} // if $obj6 already loaded

				// Add the $obj1 (Transaction) to the collection in $obj6 (Account)
				$obj6->addTransaction($obj1);

			} // if joined row is not null

				// Add objects for joined Bankbook rows

				$key7 = BankbookPeer::getPrimaryKeyHashFromRow($row, $startcol7);
				if ($key7 !== null) {
					$obj7 = BankbookPeer::getInstanceFromPool($key7);
					if (!$obj7) {
	
						$cls = BankbookPeer::getOMClass(false);

					$obj7 = new $cls();
					$obj7->hydrate($row, $startcol7);
					BankbookPeer::addInstanceToPool($obj7, $key7);
				} // if $obj7 already loaded

				// Add the $obj1 (Transaction) to the collection in $obj7 (Bankbook)
				$obj7->addTransaction($obj1);

			} // if joined row is not null

			$results[] = $obj1;
		}
		$stmt->closeCursor();
		return $results;
	}

