	/**
	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(PaymentPeer::DATABASE_NAME);

		if ($this->isColumnModified(PaymentPeer::ID)) $criteria->add(PaymentPeer::ID, $this->id);
		if ($this->isColumnModified(PaymentPeer::CREDIT_ID)) $criteria->add(PaymentPeer::CREDIT_ID, $this->credit_id);
		if ($this->isColumnModified(PaymentPeer::TRANSACTION_ID)) $criteria->add(PaymentPeer::TRANSACTION_ID, $this->transaction_id);
		if ($this->isColumnModified(PaymentPeer::NUMBER)) $criteria->add(PaymentPeer::NUMBER, $this->number);
		if ($this->isColumnModified(PaymentPeer::DATE)) $criteria->add(PaymentPeer::DATE, $this->date);
		if ($this->isColumnModified(PaymentPeer::BALANCE)) $criteria->add(PaymentPeer::BALANCE, $this->balance);
		if ($this->isColumnModified(PaymentPeer::CAPITAL)) $criteria->add(PaymentPeer::CAPITAL, $this->capital);
		if ($this->isColumnModified(PaymentPeer::INTEREST)) $criteria->add(PaymentPeer::INTEREST, $this->interest);
		if ($this->isColumnModified(PaymentPeer::STATUS)) $criteria->add(PaymentPeer::STATUS, $this->status);
		if ($this->isColumnModified(PaymentPeer::DAYS_IN_ARREAR)) $criteria->add(PaymentPeer::DAYS_IN_ARREAR, $this->days_in_arrear);
		if ($this->isColumnModified(PaymentPeer::ARREAR)) $criteria->add(PaymentPeer::ARREAR, $this->arrear);
		if ($this->isColumnModified(PaymentPeer::DISCOUNT)) $criteria->add(PaymentPeer::DISCOUNT, $this->discount);
		if ($this->isColumnModified(PaymentPeer::PAID_AT)) $criteria->add(PaymentPeer::PAID_AT, $this->paid_at);

		return $criteria;
	}

