			}

			if ($ret) {
				InvestmentPeer::doDelete($this, $con);
				$this->postDelete($con);
				// symfony_behaviors behavior
				foreach (sfMixer::getCallables('BaseInvestment:delete:post') as $callable)
				{
				  call_user_func($callable, $this, $con);
				}

