		if ($this->isColumnModified(InvestmentPeer::ASSOCIATE_ID)) $criteria->add(InvestmentPeer::ASSOCIATE_ID, $this->associate_id);
		if ($this->isColumnModified(InvestmentPeer::ACCOUNT_ID)) $criteria->add(InvestmentPeer::ACCOUNT_ID, $this->account_id);
		if ($this->isColumnModified(InvestmentPeer::PRODUCT_ID)) $criteria->add(InvestmentPeer::PRODUCT_ID, $this->product_id);
		if ($this->isColumnModified(InvestmentPeer::AMOUNT)) $criteria->add(InvestmentPeer::AMOUNT, $this->amount);
		if ($this->isColumnModified(InvestmentPeer::BALANCE)) $criteria->add(InvestmentPeer::BALANCE, $this->balance);
		if ($this->isColumnModified(InvestmentPeer::TIME_DAYS)) $criteria->add(InvestmentPeer::TIME_DAYS, $this->time_days);
		if ($this->isColumnModified(InvestmentPeer::EXPIRATION_DATE)) $criteria->add(InvestmentPeer::EXPIRATION_DATE, $this->expiration_date);
		if ($this->isColumnModified(InvestmentPeer::INTEREST_RATE)) $criteria->add(InvestmentPeer::INTEREST_RATE, $this->interest_rate);
		if ($this->isColumnModified(InvestmentPeer::TAX_RATE)) $criteria->add(InvestmentPeer::TAX_RATE, $this->tax_rate);
