		if ($this->isColumnModified(InvestmentPeer::CREATED_AT)) $criteria->add(InvestmentPeer::CREATED_AT, $this->created_at);
		if ($this->isColumnModified(InvestmentPeer::UPDATED_AT)) $criteria->add(InvestmentPeer::UPDATED_AT, $this->updated_at);

