	public function initInvestmentTransactions()
	{
		$this->collInvestmentTransactions = array();
	}

	/**
	 * Gets an array of InvestmentTransaction objects which contain a foreign key that references this object.
	 *
	 * If this collection has already been initialized with an identical Criteria, it returns the collection.
	 * Otherwise if this Investment has previously been saved, it will retrieve
	 * related InvestmentTransactions from storage. If this Investment is new, it will return
	 * an empty collection or the current collection, the criteria is ignored on a new object.
	 *
	 * @param      PropelPDO $con
	 * @param      Criteria $criteria
	 * @return     array InvestmentTransaction[]
	 * @throws     PropelException
	 */
	public function getInvestmentTransactions($criteria = null, PropelPDO $con = null)
	{
		if ($criteria === null) {
			$criteria = new Criteria(InvestmentPeer::DATABASE_NAME);
		}
		elseif ($criteria instanceof Criteria)
		{
			$criteria = clone $criteria;
		}

		if ($this->collInvestmentTransactions === null) {
			if ($this->isNew()) {
			   $this->collInvestmentTransactions = array();
			} else {

				$criteria->add(InvestmentTransactionPeer::INVESTMENT_ID, $this->id);

				InvestmentTransactionPeer::addSelectColumns($criteria);
				$this->collInvestmentTransactions = InvestmentTransactionPeer::doSelect($criteria, $con);
			}
		} else {
			// criteria has no effect for a new object
			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return the collection.


				$criteria->add(InvestmentTransactionPeer::INVESTMENT_ID, $this->id);

				InvestmentTransactionPeer::addSelectColumns($criteria);
				if (!isset($this->lastInvestmentTransactionCriteria) || !$this->lastInvestmentTransactionCriteria->equals($criteria)) {
					$this->collInvestmentTransactions = InvestmentTransactionPeer::doSelect($criteria, $con);
				}
			}
		}
		$this->lastInvestmentTransactionCriteria = $criteria;
		return $this->collInvestmentTransactions;
	}

	/**
	 * Returns the number of related InvestmentTransaction objects.
	 *
	 * @param      Criteria $criteria
	 * @param      boolean $distinct
	 * @param      PropelPDO $con
	 * @return     int Count of related InvestmentTransaction objects.
	 * @throws     PropelException
	 */
	public function countInvestmentTransactions(Criteria $criteria = null, $distinct = false, PropelPDO $con = null)
	{
		if ($criteria === null) {
			$criteria = new Criteria(InvestmentPeer::DATABASE_NAME);
		} else {
			$criteria = clone $criteria;
		}

		if ($distinct) {
			$criteria->setDistinct();
		}

		$count = null;

		if ($this->collInvestmentTransactions === null) {
			if ($this->isNew()) {
				$count = 0;
			} else {

				$criteria->add(InvestmentTransactionPeer::INVESTMENT_ID, $this->id);

				$count = InvestmentTransactionPeer::doCount($criteria, false, $con);
			}
		} else {
			// criteria has no effect for a new object
			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return count of the collection.


				$criteria->add(InvestmentTransactionPeer::INVESTMENT_ID, $this->id);

				if (!isset($this->lastInvestmentTransactionCriteria) || !$this->lastInvestmentTransactionCriteria->equals($criteria)) {
					$count = InvestmentTransactionPeer::doCount($criteria, false, $con);
				} else {
					$count = count($this->collInvestmentTransactions);
				}
			} else {
				$count = count($this->collInvestmentTransactions);
			}
		}
		return $count;
	}

	/**
	 * Method called to associate a InvestmentTransaction object to this object
	 * through the InvestmentTransaction foreign key attribute.
	 *
	 * @param      InvestmentTransaction $l InvestmentTransaction
	 * @return     void
	 * @throws     PropelException
	 */
	public function addInvestmentTransaction(InvestmentTransaction $l)
	{
		if ($this->collInvestmentTransactions === null) {
			$this->initInvestmentTransactions();
		}
		if (!in_array($l, $this->collInvestmentTransactions, true)) { // only add it if the **same** object is not already associated
			array_push($this->collInvestmentTransactions, $l);
			$l->setInvestment($this);
		}
	}


